// 1. Obtenemos todas las filas que Google Sheets nos ha dado
const todasLasCitas = items;

// 2. Filtramos: Nos quedamos solo con las que NO est√©n canceladas
// (Y nos aseguramos de que tengan un ID real para evitar filas vac√≠as)
const citasActivas = todasLasCitas.filter(item => {
  const estado = item.json.estado;
  const id = item.json.id_cita;

  // Si no tiene ID, no cuenta. Si el estado es "Cancelada", no cuenta.
  if (!id) return false;
  if (estado === 'Cancelada') return false;

  return true; // Es una cita v√°lida
});

// 3. Devolvemos el conteo total
return [{
  json: {
    total_ocupadas: citasActivas.length
  }
}];
