// n8n Function node ‚Äî limpiar texto y mantener el resto del payload
return items.map(item => {
  // Ahora toma la variable 'output' del nodo anterior
  const originalText = (item.json.output ?? '').toString();

  let cleaned = originalText
    // Entidades HTML b√°sicas
    .replace(/&nbsp;/gi, ' ')
    .replace(/&amp;/gi, '&')
    .replace(/&lt;/gi, '<')
    .replace(/&gt;/gi, '>')
    .replace(/&quot;/gi, '"')
    .replace(/&#39;/gi, "'")
    // <br> -> espacio y quitar el resto de etiquetas
    .replace(/<br\s*\/?>/gi, ' ')
    .replace(/<[^>]+>/g, '')
    // Normalizar saltos de l√≠nea y tabs
    .replace(/\r?\n|\r/g, ' ')
    .replace(/\t+/g, ' ')
    // Mantener letras/n√∫meros y puntuaci√≥n com√∫n (Unicode con flag 'u')
    .replace(/[^\p{L}\p{N}.,:;¬ø?¬°!()"'¬´¬ª\s-]+/gu, ' ')
    // Colapsar espacios y recortar
    .replace(/\s+/g, ' ')
    .trim();

  return {
    json: {
      ...item.json,
      output: cleaned, // Sobrescribe output con la versi√≥n limpia
    },
  };
});
