const items = $input.all();

// 1. VERIFICACIÓN ROBUSTA DE HUECO LIBRE
// Comprobamos si la lista está vacía O si el primer elemento NO tiene propiedad 'start'.
// Esto cubre el caso donde "Always Output Data" envía un item vacío.
if (items.length === 0 || !items[0].json.start) {
  return [{
    json: {
      status: "free",
      message: "El horario está libre. Puedes reservar."
    }
  }];
}

// 2. SI HAY CITAS (HUECO OCUPADO)
// Usamos "optional chaining" (?.) para evitar errores si falta algún dato raro
const busySlots = items.map(item => {
  const start = item.json.start?.dateTime || item.json.start?.date || "Hora desconocida";
  const end = item.json.end?.dateTime || item.json.end?.date || "Hora desconocida";

  return `Ocupado desde ${start} hasta ${end}`;
}).join(". ");

// 3. DEVOLVER RESPUESTA
return [{
  json: {
    status: "busy",
    message: "Ese horario no está disponible. Conflictos: " + busySlots
  }
}];

const date = new Date($json.date + "T12:00:00");
const dayOfWeek = date.getDay(); // 0 = Domingo, 6 = Sábado

// 1. VERIFICAR DOMINGO
if (dayOfWeek === 0) {
  return [{
    json: {
      status: "closed",
      message: "Ese día cae domingo y estamos cerrados. ¿Qué otro día te vendría bien?"
    }
  }];
}

// 2. VERIFICAR HORARIO
// Extraer hora del fromTime (formato "HH:MM:SS")
const hour = parseInt($json.fromTime.split(":")[0]);

// Sábado: solo 10:00-13:30
if (dayOfWeek === 6) {
  if (hour < 10 || hour >= 14) {
    return [{
      json: {
        status: "closed",
        message: "Los sábados solo atendemos de 10:00 a 13:30. ¿Te viene bien en ese horario?"
      }
    }];
  }
}

// Lunes-Viernes: rechazar 14:00-16:00 y 20:00-09:00
if (dayOfWeek >= 1 && dayOfWeek <= 5) {
  if (hour >= 14 && hour < 16) {
    return [{
      json: {
        status: "closed",
        message: "De 14:00 a 16:00 estamos cerrados. Trabajamos de 09:00 a 13:30 y de 16:00 a 19:30. ¿Qué horario te viene mejor?"
      }
    }];
  }
  if (hour >= 20 || hour < 9) {
    return [{
      json: {
        status: "closed",
        message: "Ese horario está fuera de nuestro servicio. Trabajamos de 09:00 a 13:30 y de 16:00 a 19:30. ¿Qué horario te viene mejor?"
      }
    }];
  }
}

// 3. TODO OK - Continuar con los datos originales
return [{
  json: {
    date: $json.date,
    fromTime: $json.fromTime,
    toTime: $json.toTime
  }
}];

