parameters.jsCode:
// El JSON del GPT viene como texto dentro de message.content
const raw = $json.message.content;

// Convertimos ese texto en un JSON real
let parsed = {};

try {
  parsed = JSON.parse(raw);
} catch (e) {
  // Si el modelo a√±adi√≥ texto fuera del JSON, limpiamos
  const match = raw.match(/\{[\s\S]*\}/);
  if (!match) {
    throw new Error("No se pudo convertir el contenido a JSON.");
  }
  parsed = JSON.parse(match[0]);
}

// Devolvemos el JSON convertido como salida del nodo Function
return parsed;
