// 1. Recogemos los datos
// Nota: inputData.company viene del nuevo campo que a√±adimos al HTML
const inputData = items[0].json.body || items[0].json;

const name = inputData.name || 'Desconocido';
const company = inputData.company || 'No indicada'; // <--- NUEVO CAMPO
const email = inputData.email || 'No especificado';
const message = inputData.message || 'Sin mensaje';
const userAgent = inputData.userAgent || '';
const date = new Date().toLocaleString('es-ES', { timeZone: 'Europe/Madrid' });

// --- L√ìGICA T√âCNICA DE DISPOSITIVO ---
let device = "Escritorio / Desconocido";

if (/iPhone/i.test(userAgent)) {
    const iosVersion = userAgent.match(/OS\s([\d_]+)/)?.[1]?.replace(/_/g, '.') || "";
    device = `Apple iPhone - iOS ${iosVersion}`;
} else if (/iPad/i.test(userAgent)) {
    device = "Apple iPad";
} else if (/Android/i.test(userAgent)) {
    const match = userAgent.match(/;\s([^;]+?)\sBuild\//);
    device = match ? `Android: ${match[1].trim()}` : "Android (Modelo no detectado)";
} else if (/Macintosh/i.test(userAgent)) {
    device = "Apple Mac / MacBook";
} else if (/Windows/i.test(userAgent)) {
    device = "PC Windows";
} else if (/Linux/i.test(userAgent)) {
    device = "Sistema Linux";
}

// 2. HTML PROFESIONAL (Actualizado con Empresa)
const emailHtml = `
<!DOCTYPE html>
<html>
<head>
  <style>
    @import url('https://fonts.googleapis.com/css2?family=Poppins:wght@400;500;700&display=swap');
  </style>
</head>
<body style="margin: 0; padding: 0; background-color: #ECEDF1; font-family: 'Poppins', Helvetica, Arial, sans-serif; -webkit-font-smoothing: antialiased;">

  <table width="100%" border="0" cellspacing="0" cellpadding="0" style="background-color: #ECEDF1; padding: 40px 0;">
    <tr>
      <td align="center">

        <table width="600" border="0" cellspacing="0" cellpadding="0" style="background-color: #ffffff; border-radius: 12px; overflow: hidden; box-shadow: 0 2px 15px rgba(0,0,0,0.08); border: 1px solid #E5E7EB;">

          <tr>
            <td style="background-color: #006DE7; padding: 30px 40px;">
              <h1 style="color: #ffffff; margin: 0; font-size: 22px; font-weight: 600; letter-spacing: -0.5px;">
                Nuevo Lead B2B
              </h1>
              <p style="color: rgba(255,255,255,0.9); margin: 5px 0 0 0; font-size: 13px; font-weight: 400;">
                Origen: Formulario Web
              </p>
            </td>
          </tr>

          <tr>
            <td style="padding: 40px;">

              <table width="100%" border="0" cellspacing="0" cellpadding="0" style="margin-bottom: 30px;">

                <tr>
                  <td width="30%" style="padding-bottom: 15px; color: #6B7280; font-size: 12px; font-weight: 600; text-transform: uppercase; letter-spacing: 0.5px;">Nombre</td>
                  <td width="70%" style="padding-bottom: 15px; color: #111827; font-size: 16px; font-weight: 500;">${name}</td>
                </tr>

                <tr>
                  <td style="padding-bottom: 15px; color: #6B7280; font-size: 12px; font-weight: 600; text-transform: uppercase; letter-spacing: 0.5px;">Empresa</td>
                  <td style="padding-bottom: 15px; color: #111827; font-size: 16px; font-weight: 500;">${company}</td>
                </tr>

                <tr>
                  <td style="padding-bottom: 15px; color: #6B7280; font-size: 12px; font-weight: 600; text-transform: uppercase; letter-spacing: 0.5px;">Email</td>
                  <td style="padding-bottom: 15px; color: #006DE7; font-size: 16px; font-weight: 500;"><a href="mailto:${email}" style="color: #006DE7; text-decoration: none;">${email}</a></td>
                </tr>
              </table>

              <div style="background-color: #F8FAFC; border: 1px solid #E2E8F0; border-radius: 8px; padding: 15px; margin-bottom: 30px;">
                 <p style="margin: 0 0 5px 0; color: #475569; font-size: 11px; font-weight: 700; text-transform: uppercase; letter-spacing: 0.5px;">Dispositivo / Modelo</p>
                 <p style="margin: 0; font-size: 15px; color: #0F172A; font-family: monospace, sans-serif; font-weight: 600;">
