`;

return [
  {
    json: {
      // He actualizado el asunto para que veas la empresa directamente en la bandeja de entrada
      subject: `Nuevo Lead: ${name} (${company})`,
      html: emailHtml,
      replyTo: email,
      deviceModel: device,
      company: company // Dato extra por si lo usas en nodos siguientes (Airtable/Notion)
    }
  }
